function execute(){
    var max = 4000000;
    var fibb = getFibonacciSequence(max);
    var evenFibb =[];

    for (var i=0;i < fibb.length;i++){
       if(fibb[i] % 2 === 0)
            evenFibb.push(fibb[i]);
        // I could grab every other one instead...meh
    }
    output(sumArray(evenFibb));
}
function getFibonacciSequence(max){
    var fibb = [1,2];
    var currentNumber = 2;
    //get all fibonacci numbers up to max
    for (var i=1;currentNumber < max;i++){
        currentNumber = fibb[i]+fibb[i-1];
        if(currentNumber < max){
            fibb.push(currentNumber);
        }
    }
    return fibb;
}